import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { seedLoyaltyOffers, seedDummyUsers, seedSampleChurnEmails } from "./storage";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Seed initial data
  await seedLoyaltyOffers();
  await seedDummyUsers();
  await seedSampleChurnEmails();

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || 'development-session-secret-' + Math.random().toString(36),
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    // Use the first domain from REPLIT_DOMAINS if hostname is localhost/127.0.0.1
    const hostname = req.hostname === '127.0.0.1' || req.hostname === 'localhost' 
      ? process.env.REPLIT_DOMAINS!.split(",")[0] 
      : req.hostname;
    passport.authenticate(`replitauth:${hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    // Use the first domain from REPLIT_DOMAINS if hostname is localhost/127.0.0.1
    const hostname = req.hostname === '127.0.0.1' || req.hostname === 'localhost' 
      ? process.env.REPLIT_DOMAINS!.split(",")[0] 
      : req.hostname;
    passport.authenticate(`replitauth:${hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage, seedLoyaltyOffers, seedDummyUsers } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import {
  insertLoyaltyOfferSchema,
  insertOfferRedemptionSchema,
  insertOutreachActionSchema,
  insertChatConversationSchema,
  insertWorkoutPlanSchema,
} from "@shared/schema";
import {
  generateRetentionStrategies,
  generateLoyaltyOffers,
  sendMessageToChat,
  generateWorkoutPlan,
  generateSalesEmail,
} from "./services/geminiService";
import { EmailService } from "./services/emailService";
import { EmailTrackingService } from "./services/emailTrackingService";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Role toggle endpoint for testing personas
  app.post('/api/user/toggle-role', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { role } = req.body;

      if (!role || !['member', 'staff'].includes(role)) {
        return res.status(400).json({ message: 'Invalid role. Must be "member" or "staff"' });
      }

      const updatedUser = await storage.upsertUser({
        id: userId,
        role,
        updatedAt: new Date()
      });

      res.json({ message: 'Role updated successfully', user: updatedUser });
    } catch (error) {
      console.error('Error toggling user role:', error);
      res.status(500).json({ message: 'Failed to update user role' });
    }
  });

  // Loyalty offers endpoints
  app.get('/api/loyalty-offers', isAuthenticated, async (req, res) => {
    try {
      const offers = await storage.getLoyaltyOffers();
      res.json(offers);
    } catch (error) {
      console.error("Error fetching loyalty offers:", error);
      res.status(500).json({ message: "Failed to fetch loyalty offers" });
    }
  });

  app.post('/api/loyalty-offers', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const offerData = insertLoyaltyOfferSchema.parse({
        ...req.body,
        createdBy: user.id,
      });

      const offer = await storage.createLoyaltyOffer(offerData);
      res.json(offer);
    } catch (error) {
      console.error("Error creating loyalty offer:", error);
      res.status(500).json({ message: "Failed to create loyalty offer" });
    }
  });

  app.delete('/api/loyalty-offers/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      await storage.deactivateLoyaltyOffer(req.params.id);
      res.json({ success: true });
    } catch (error) {
      console.error("Error deactivating loyalty offer:", error);
      res.status(500).json({ message: "Failed to deactivate loyalty offer" });
    }
  });

  // Offer redemption endpoints
  app.get('/api/user/redemptions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const redemptions = await storage.getUserRedemptions(userId);
      res.json(redemptions);
    } catch (error) {
      console.error("Error fetching user redemptions:", error);
      res.status(500).json({ message: "Failed to fetch redemptions" });
    }
  });

  app.post('/api/offers/:offerId/redeem', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'member') {
        return res.status(403).json({ message: "Member access required" });
      }

      const offers = await storage.getLoyaltyOffers();
      const offer = offers.find(o => o.id === req.params.offerId);

      if (!offer) {
        return res.status(404).json({ message: "Offer not found" });
      }

      if ((user.loyaltyPoints || 0) < offer.points) {
        return res.status(400).json({ message: "Insufficient points" });
      }

      // Check if already redeemed
      const userRedemptions = await storage.getUserRedemptions(userId);
      const alreadyRedeemed = userRedemptions.some(r => r.offerId === offer.id);

      if (alreadyRedeemed) {
        return res.status(400).json({ message: "Offer already redeemed" });
      }

      const redemptionData = insertOfferRedemptionSchema.parse({
        userId,
        offerId: offer.id,
        pointsSpent: offer.points,
      });

      const redemption = await storage.redeemOffer(redemptionData);
      res.json(redemption);
    } catch (error) {
      console.error("Error redeeming offer:", error);
      res.status(500).json({ message: "Failed to redeem offer" });
    }
  });

  // Member management endpoints (Staff only)
  app.get('/api/staff/at-risk-members', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const atRiskMembers = await storage.getAtRiskMembers();
      res.json(atRiskMembers);
    } catch (error) {
      console.error("Error fetching at-risk members:", error);
      res.status(500).json({ message: "Failed to fetch at-risk members" });
    }
  });

  app.get('/api/staff/member/:memberId/outreach-history', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const history = await storage.getMemberOutreachHistory(req.params.memberId);
      res.json(history);
    } catch (error) {
      console.error("Error fetching outreach history:", error);
      res.status(500).json({ message: "Failed to fetch outreach history" });
    }
  });

  app.post('/api/staff/outreach-action', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const actionData = insertOutreachActionSchema.parse({
        ...req.body,
        staffId: user.id,
      });

      const action = await storage.createOutreachAction(actionData);
      res.json(action);
    } catch (error) {
      console.error("Error creating outreach action:", error);
      res.status(500).json({ message: "Failed to create outreach action" });
    }
  });

  // AI-powered endpoints
  app.post('/api/ai/retention-strategies', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { memberProfile } = req.body;
      if (!memberProfile) {
        return res.status(400).json({ message: "Member profile is required" });
      }

      const strategies = await generateRetentionStrategies(memberProfile);
      res.json({ strategies });
    } catch (error) {
      console.error("Error generating retention strategies:", error);
      res.status(500).json({ message: "Failed to generate retention strategies" });
    }
  });

  app.post('/api/ai/generate-loyalty-offers', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { targetCriteria } = req.body;
      if (!targetCriteria) {
        return res.status(400).json({ message: "Target criteria is required" });
      }

      const offers = await generateLoyaltyOffers(targetCriteria);
      res.json(offers);
    } catch (error) {
      console.error("Error generating loyalty offers:", error);
      res.status(500).json({ message: "Failed to generate loyalty offers" });
    }
  });

  app.post('/api/ai/workout-plan', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'member') {
        return res.status(403).json({ message: "Member access required" });
      }

      const { goals, healthData } = req.body;
      if (!goals) {
        return res.status(400).json({ message: "Goals are required" });
      }
      if (!healthData) {
        return res.status(400).json({ message: "Health data is required" });
      }

      const workoutPlan = await generateWorkoutPlan(goals, healthData);

      // Save to database
      const planData = insertWorkoutPlanSchema.parse({
        userId,
        title: workoutPlan.planTitle,
        goals: `${goals} | Health Profile: Age ${healthData.age}, Fitness Level: ${healthData.fitnessLevel}, Experience: ${healthData.exerciseExperience}${healthData.medicalConditions ? ', Medical Notes: ' + healthData.medicalConditions : ''}`,
        weeklySchedule: workoutPlan.weeklySchedule,
      });

      await storage.createWorkoutPlan(planData);

      res.json(workoutPlan);
    } catch (error) {
      console.error("Error generating workout plan:", error);
      res.status(500).json({ message: "Failed to generate workout plan" });
    }
  });

  // Churn email endpoints
  app.get('/api/staff/churn-emails', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const emails = await storage.getPendingChurnEmails();
      res.json(emails);
    } catch (error) {
      console.error("Error fetching churn emails:", error);
      res.status(500).json({ message: "Failed to fetch churn emails" });
    }
  });

  app.post('/api/staff/churn-emails/:id/approve', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const email = await storage.approveChurnEmail(req.params.id, user.id);
      res.json(email);
    } catch (error) {
      console.error("Error approving churn email:", error);
      res.status(500).json({ message: "Failed to approve churn email" });
    }
  });

  app.post('/api/staff/churn-emails/:id/reject', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const email = await storage.rejectChurnEmail(req.params.id, user.id);
      res.json(email);
    } catch (error) {
      console.error("Error rejecting churn email:", error);
      res.status(500).json({ message: "Failed to reject churn email" });
    }
  });

  app.post('/api/staff/churn-emails/:id/send', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const email = await storage.markChurnEmailSent(req.params.id);
      res.json(email);
    } catch (error) {
      console.error("Error marking churn email as sent:", error);
      res.status(500).json({ message: "Failed to mark churn email as sent" });
    }
  });

  app.post('/api/staff/churn-emails/generate', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { memberId } = req.body;
      if (!memberId) {
        return res.status(400).json({ message: "Member ID is required" });
      }

      const email = await storage.checkAndCreateChurnEmail(memberId);
      res.json(email);
    } catch (error) {
      console.error("Error generating churn email:", error);
      res.status(500).json({ message: "Failed to generate churn email" });
    }
  });

  // Chatbot endpoints
  app.post('/api/chat/message', async (req, res) => {
    try {
      const { message, sessionId } = req.body;
      if (!message || !sessionId) {
        return res.status(400).json({ message: "Message and session ID are required" });
      }

      // Get existing conversation or create new one
      let conversation = await storage.getChatConversation(sessionId);
      if (!conversation) {
        conversation = await storage.createChatConversation({
          sessionId,
          messages: [],
        });
      }

      const response = await sendMessageToChat(message, conversation.messages as any[]);

      // Update conversation with new messages
      const updatedMessages = [
        ...conversation.messages as any[],
        { role: 'user', content: message },
        { role: 'assistant', content: response.content }
      ];

      await storage.updateChatConversation(sessionId, {
        messages: updatedMessages,
        contactEmail: response.contactEmail || conversation.contactEmail,
        contactName: response.contactName || conversation.contactName,
        tourBooked: response.tourBooked || conversation.tourBooked,
      });

      res.json(response);
    } catch (error) {
      console.error("Error processing chat message:", error);
      res.status(500).json({ message: "Failed to process chat message" });
    }
  });

  // Dashboard metrics endpoint
  app.get('/api/staff/metrics', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const atRiskMembers = await storage.getAtRiskMembers();

      // Mock metrics for demo (in production, these would be calculated from real data)
      const metrics = {
        totalMembers: 2847,
        atRiskMembers: atRiskMembers.length,
        churnRate: "3.2%",
        outreachToday: 8,
      };

      res.json(metrics);
    } catch (error) {
      console.error("Error fetching metrics:", error);
      res.status(500).json({ message: "Failed to fetch metrics" });
    }
  });

  // Sales email generation endpoint
  app.post('/api/ai/sales-email', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { prompt, systemInstruction, model } = req.body;

      if (!prompt || !systemInstruction) {
        return res.status(400).json({ message: "Prompt and system instruction are required" });
      }

      const emailContent = await generateSalesEmail(prompt, systemInstruction, model);
      res.json(emailContent);
    } catch (error) {
      console.error("Error generating sales email:", error);
      res.status(500).json({ message: "Failed to generate sales email" });
    }
  });

  // AI Chat endpoint for churn analysis
  app.post('/api/ai/chat', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { message, context } = req.body;
      if (!message) {
        return res.status(400).json({ message: "Message is required" });
      }

      // Create a contextual conversation for churn analysis
      const conversationHistory = [
        {
          role: 'system',
          content: `You are a Customer Churn Analysis AI assistant for a premium health and wellness club. 
          Your role is to help staff with customer retention insights, churn prevention strategies, and data analysis.
          Be professional, analytical, and provide actionable recommendations.

          Context: ${context || 'general_churn_analysis'}`
        }
      ];

      const result = await sendMessageToChat(message, conversationHistory);
      res.json({ response: result.content });
    } catch (error) {
      console.error("Error processing AI chat:", error);
      res.status(500).json({ message: "Failed to process AI chat" });
    }
  });

  // Lead capture endpoint - saves ChatBot conversations to CRM
  app.post("/api/leads", async (req, res) => {
    try {
      const { name, email, phone, fitnessGoal, frequency, location, language, sessionId } = req.body;

      if (!name || !email) {
        return res.status(400).json({ 
          success: false, 
          error: "Name and email are required" 
        });
      }

      // Save lead as a prospect in the users table
      const leadData = {
        email: email.toLowerCase(),
        firstName: name.split(' ')[0] || name,
        lastName: name.split(' ').slice(1).join(' ') || '',
        role: 'prospect', // Mark as prospect rather than member
        membershipType: 'potential', // Special type for leads
        loyaltyPoints: 0,
        joinDate: null, // Not a member yet
        lastVisit: null,
      };

      const savedLead = await storage.upsertUser(leadData);

      // Save the conversation data with lead details
      const conversationData = {
        sessionId,
        messages: [{
          role: 'system',
          content: `Lead captured: ${name} (${email}) - Fitness Goal: ${fitnessGoal}, Frequency: ${frequency}, Location: ${location}, Language: ${language}`
        }],
        isComplete: true,
        contactEmail: email,
        contactName: name,
        tourBooked: false
      };

      // Save or update the conversation
      const existingConversation = await storage.getChatConversation(sessionId);
      if (existingConversation) {
        await storage.updateChatConversation(sessionId, conversationData);
      } else {
        await storage.createChatConversation(conversationData);
      }

      console.log("New lead saved to CRM:", {
        id: savedLead.id,
        name,
        email,
        phone,
        fitnessGoal,
        frequency,
        location,
        language,
        sessionId,
        timestamp: new Date().toISOString()
      });

      res.json({ 
        success: true, 
        message: "Lead saved to CRM successfully",
        leadId: savedLead.id
      });
    } catch (error) {
      console.error("Error saving lead to CRM:", error);
      res.status(500).json({ success: false, error: "Failed to save lead to CRM" });
    }
  });

  app.post('/api/send-email', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { to, subject, content, prospectName } = req.body;

      if (!to || !subject || !content || !prospectName) {
        return res.status(400).json({
          message: "Email address, subject, content, and prospect name are required"
        });
      }

      // Validate email format
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(to)) {
        return res.status(400).json({ message: "Invalid email address format" });
      }

      const result = await EmailService.sendSalesEmail({
        to,
        subject,
        content,
        prospectName
      });

      if (result.success) {
        res.json({
          success: true,
          messageId: result.messageId,
          message: "Email sent successfully"
        });
      } else {
        res.status(500).json({
          success: false,
          error: result.error,
          message: "Failed to send email"
        });
      }
    } catch (error) {
      console.error("Error sending email:", error);
      res.status(500).json({ message: "Failed to send email" });
    }
  });

  // Email service status endpoint
  app.get('/api/email/status', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const status = EmailService.getEmailServiceStatus();
      res.json(status);
    } catch (error) {
      console.error("Error fetching email service status:", error);
      res.status(500).json({ message: "Failed to fetch email service status" });
    }
  });

  // Email tracking endpoints
  app.get('/api/track/:trackingId', async (req, res) => {
    try {
      const { trackingId } = req.params;

      // Log the link click
      await EmailTrackingService.logLinkClicked(trackingId, {
        userAgent: req.headers['user-agent'],
        ip: req.ip,
        timestamp: new Date().toISOString(),
      });

      // Redirect to virtual tour page with tracking parameter
      res.redirect(`/virtual-tour?track=${trackingId}`);
    } catch (error) {
      console.error("Error tracking email click:", error);
      // Still redirect to home page even if tracking fails
      res.redirect('/');
    }
  });

  // Virtual tour endpoint
  app.get('/api/virtual-tour/:trackingId', async (req, res) => {
    try {
      const { trackingId } = req.params;

      // Log the tour view
      await EmailTrackingService.logTourViewed(trackingId, {
        userAgent: req.headers['user-agent'],
        ip: req.ip,
        timestamp: new Date().toISOString(),
      });

      // Get prospect information
      const interaction = await EmailTrackingService.getEmailInteractionByTrackingId(trackingId);

      if (interaction) {
        res.json({
          success: true,
          prospectName: interaction.prospectName,
          prospectEmail: interaction.prospectEmail,
          trackingId,
          message: 'Virtual tour tracking recorded'
        });
      } else {
        res.status(404).json({
          success: false,
          message: 'Tracking ID not found'
        });
      }
    } catch (error) {
      console.error("Error tracking virtual tour:", error);
      res.status(500).json({
        success: false,
        message: 'Failed to track virtual tour'
      });
    }
  });

  // Get prospect engagement data
  app.get('/api/prospect/:email/engagement', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (!user || user.role !== 'staff') {
        return res.status(403).json({ message: "Staff access required" });
      }

      const { email } = req.params;
      const summary = await EmailTrackingService.getProspectEngagementSummary(email);
      const interactions = await EmailTrackingService.getEmailInteractionsByProspect(email);

      res.json({
        summary,
        interactions
      });
    } catch (error) {
      console.error("Error fetching prospect engagement:", error);
      res.status(500).json({ message: "Failed to fetch engagement data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  loyaltyOffers,
  offerRedemptions,
  outreachActions,
  chatConversations,
  workoutPlans,
  churnEmails,
  emailInteractions,
  leads,
  type User,
  type UpsertUser,
  type LoyaltyOffer,
  type InsertLoyaltyOffer,
  type OfferRedemption,
  type InsertOfferRedemption,
  type OutreachAction,
  type InsertOutreachAction,
  type ChatConversation,
  type InsertChatConversation,
  type WorkoutPlan,
  type InsertWorkoutPlan,
  type ChurnEmail,
  type InsertChurnEmail,
  type EmailInteraction,
  type InsertEmailInteraction,
  type Lead,
  type InsertLead,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  getAllUsers(): Promise<User[]>;
  getAllMembers(): Promise<User[]>;
  getAllStaff(): Promise<User[]>;

  // Loyalty operations
  getLoyaltyOffers(): Promise<LoyaltyOffer[]>;
  createLoyaltyOffer(offer: InsertLoyaltyOffer): Promise<LoyaltyOffer>;
  deactivateLoyaltyOffer(id: string): Promise<void>;
  getUserRedemptions(userId: string): Promise<OfferRedemption[]>;
  redeemOffer(redemption: InsertOfferRedemption): Promise<OfferRedemption>;

  // Member management
  getAtRiskMembers(): Promise<User[]>;
  updateUserLastVisit(userId: string): Promise<void>;
  updateUserLoyaltyPoints(userId: string, points: number): Promise<void>;

  // Outreach operations
  createOutreachAction(action: InsertOutreachAction): Promise<OutreachAction>;
  getMemberOutreachHistory(memberId: string): Promise<OutreachAction[]>;

  // Chat operations
  getChatConversation(sessionId: string): Promise<ChatConversation | undefined>;
  createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation>;
  updateChatConversation(sessionId: string, updates: Partial<ChatConversation>): Promise<void>;

  // Workout plans
  createWorkoutPlan(plan: InsertWorkoutPlan): Promise<WorkoutPlan>;
  getUserWorkoutPlans(userId: string): Promise<WorkoutPlan[]>;

  // Churn email operations
  createChurnEmail(email: InsertChurnEmail): Promise<ChurnEmail>;
  getPendingChurnEmails(): Promise<ChurnEmail[]>;
  getChurnEmailsByMember(memberId: string): Promise<ChurnEmail[]>;
  approveChurnEmail(emailId: string, staffId: string): Promise<ChurnEmail>;
  rejectChurnEmail(emailId: string, staffId: string): Promise<ChurnEmail>;
  markChurnEmailSent(emailId: string): Promise<ChurnEmail>;
  getRiskLevel(member: User): { level: 'low' | 'medium' | 'high', band: string, percentage: number };
  checkAndCreateChurnEmail(memberId: string): Promise<ChurnEmail | null>;

  // Email interaction tracking operations
  createEmailInteraction(interaction: InsertEmailInteraction): Promise<EmailInteraction>;
  getEmailInteractionsByProspect(email: string): Promise<EmailInteraction[]>;
  getEmailInteractionByTrackingId(trackingId: string): Promise<EmailInteraction | undefined>;
  updateEmailInteraction(id: string, updates: Partial<EmailInteraction>): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(users.createdAt);
  }

  async getAllMembers(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(eq(users.role, "member"))
      .orderBy(users.createdAt);
  }

  async getAllStaff(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(eq(users.role, "staff"))
      .orderBy(users.createdAt);
  }

  // Loyalty operations
  async getLoyaltyOffers(): Promise<LoyaltyOffer[]> {
    return await db
      .select()
      .from(loyaltyOffers)
      .where(eq(loyaltyOffers.isActive, true))
      .orderBy(loyaltyOffers.points);
  }

  async createLoyaltyOffer(offer: InsertLoyaltyOffer): Promise<LoyaltyOffer> {
    const [newOffer] = await db
      .insert(loyaltyOffers)
      .values(offer)
      .returning();
    return newOffer;
  }

  async deactivateLoyaltyOffer(id: string): Promise<void> {
    await db
      .update(loyaltyOffers)
      .set({ isActive: false })
      .where(eq(loyaltyOffers.id, id));
  }

  async getUserRedemptions(userId: string): Promise<OfferRedemption[]> {
    return await db
      .select()
      .from(offerRedemptions)
      .where(eq(offerRedemptions.userId, userId))
      .orderBy(desc(offerRedemptions.redeemedAt));
  }

  async redeemOffer(redemption: InsertOfferRedemption): Promise<OfferRedemption> {
    const [newRedemption] = await db
      .insert(offerRedemptions)
      .values(redemption)
      .returning();

    // Deduct points from user
    await db
      .update(users)
      .set({ 
        loyaltyPoints: sql`${users.loyaltyPoints} - ${redemption.pointsSpent}` 
      })
      .where(eq(users.id, redemption.userId));

    return newRedemption;
  }

  // Member management
  async getAtRiskMembers(): Promise<User[]> {
    // Simulate churn risk calculation - members who haven't visited in 5+ days
    const fiveDaysAgo = new Date();
    fiveDaysAgo.setDate(fiveDaysAgo.getDate() - 5);

    return await db
      .select()
      .from(users)
      .where(
        and(
          eq(users.role, "member"),
          sql`${users.lastVisit} < ${fiveDaysAgo} OR ${users.lastVisit} IS NULL`
        )
      )
      .orderBy(desc(users.lastVisit));
  }

  async updateUserLastVisit(userId: string): Promise<void> {
    await db
      .update(users)
      .set({ lastVisit: new Date() })
      .where(eq(users.id, userId));
  }

  async updateUserLoyaltyPoints(userId: string, points: number): Promise<void> {
    await db
      .update(users)
      .set({ loyaltyPoints: points })
      .where(eq(users.id, userId));
  }

  // Outreach operations
  async createOutreachAction(action: InsertOutreachAction): Promise<OutreachAction> {
    const [newAction] = await db
      .insert(outreachActions)
      .values(action)
      .returning();
    return newAction;
  }

  async getMemberOutreachHistory(memberId: string): Promise<OutreachAction[]> {
    const result = await db
      .select({
        id: outreachActions.id,
        memberId: outreachActions.memberId,
        staffId: outreachActions.staffId,
        actionType: outreachActions.actionType,
        notes: outreachActions.notes,
        createdAt: outreachActions.createdAt,
        staffName: sql<string>`${users.firstName} || ' ' || ${users.lastName}`,
      })
      .from(outreachActions)
      .leftJoin(users, eq(outreachActions.staffId, users.id))
      .where(eq(outreachActions.memberId, memberId))
      .orderBy(desc(outreachActions.createdAt));
    return result as OutreachAction[];
  }

  // Chat operations
  async getChatConversation(sessionId: string): Promise<ChatConversation | undefined> {
    const [conversation] = await db
      .select()
      .from(chatConversations)
      .where(eq(chatConversations.sessionId, sessionId));
    return conversation;
  }

  async createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation> {
    const [newConversation] = await db
      .insert(chatConversations)
      .values(conversation)
      .returning();
    return newConversation;
  }

  async updateChatConversation(sessionId: string, updates: Partial<ChatConversation>): Promise<void> {
    await db
      .update(chatConversations)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(chatConversations.sessionId, sessionId));
  }

  // Workout plans
  async createWorkoutPlan(plan: InsertWorkoutPlan): Promise<WorkoutPlan> {
    const [newPlan] = await db
      .insert(workoutPlans)
      .values(plan)
      .returning();
    return newPlan;
  }

  async getUserWorkoutPlans(userId: string): Promise<WorkoutPlan[]> {
    return await db
      .select()
      .from(workoutPlans)
      .where(eq(workoutPlans.userId, userId))
      .orderBy(desc(workoutPlans.createdAt));
  }

  // Churn email operations
  async createChurnEmail(email: InsertChurnEmail): Promise<ChurnEmail> {
    const [newEmail] = await db
      .insert(churnEmails)
      .values(email)
      .returning();
    return newEmail;
  }

  async getPendingChurnEmails(): Promise<ChurnEmail[]> {
    const result = await db
      .select({
        id: churnEmails.id,
        memberId: churnEmails.memberId,
        staffId: churnEmails.staffId,
        subject: churnEmails.subject,
        content: churnEmails.content,
        riskLevel: churnEmails.riskLevel,
        currentRiskBand: churnEmails.currentRiskBand,
        previousRiskBand: churnEmails.previousRiskBand,
        memberProfile: churnEmails.memberProfile,
        status: churnEmails.status,
        approvedBy: churnEmails.approvedBy,
        approvedAt: churnEmails.approvedAt,
        sentAt: churnEmails.sentAt,
        createdAt: churnEmails.createdAt,
        updatedAt: churnEmails.updatedAt,
        memberName: sql<string>`${users.firstName} || ' ' || ${users.lastName}`,
        memberEmail: users.email,
        memberMembershipType: users.membershipType,
      })
      .from(churnEmails)
      .leftJoin(users, eq(churnEmails.memberId, users.id))
      .where(eq(churnEmails.status, "pending"))
      .orderBy(desc(churnEmails.createdAt));
    return result as ChurnEmail[];
  }

  async getChurnEmailsByMember(memberId: string): Promise<ChurnEmail[]> {
    return await db
      .select()
      .from(churnEmails)
      .where(eq(churnEmails.memberId, memberId))
      .orderBy(desc(churnEmails.createdAt));
  }

  async approveChurnEmail(emailId: string, staffId: string): Promise<ChurnEmail> {
    const [updatedEmail] = await db
      .update(churnEmails)
      .set({
        status: "approved",
        approvedBy: staffId,
        approvedAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(churnEmails.id, emailId))
      .returning();
    return updatedEmail;
  }

  async rejectChurnEmail(emailId: string, staffId: string): Promise<ChurnEmail> {
    const [updatedEmail] = await db
      .update(churnEmails)
      .set({
        status: "rejected",
        approvedBy: staffId,
        approvedAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(churnEmails.id, emailId))
      .returning();
    return updatedEmail;
  }

  async markChurnEmailSent(emailId: string): Promise<ChurnEmail> {
    const [updatedEmail] = await db
      .update(churnEmails)
      .set({
        status: "sent",
        sentAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(churnEmails.id, emailId))
      .returning();
    return updatedEmail;
  }

  // Helper function to determine risk level and band
  getRiskLevel(member: User): { level: 'low' | 'medium' | 'high', band: string, percentage: number } {
    if (!member.lastVisit) return { level: "high", band: "never-visited", percentage: 95 };

    const daysSinceLastVisit = Math.floor(
      (Date.now() - new Date(member.lastVisit).getTime()) / (1000 * 60 * 60 * 24)
    );

    if (daysSinceLastVisit > 10) return { level: "high", band: "high-risk", percentage: 89 };
    if (daysSinceLastVisit > 7) return { level: "medium", band: "medium-risk", percentage: 76 };
    if (daysSinceLastVisit > 5) return { level: "low", band: "low-risk", percentage: 65 };
    return { level: "low", band: "active", percentage: 25 };
  }

  // Check if member needs a churn prevention email
  async checkAndCreateChurnEmail(memberId: string): Promise<ChurnEmail | null> {
    const member = await this.getUser(memberId);
    if (!member || member.role !== "member") return null;

    const currentRisk = this.getRiskLevel(member);

    // Check if there's already a recent email for this risk level
    const recentEmails = await db
      .select()
      .from(churnEmails)
      .where(
        and(
          eq(churnEmails.memberId, memberId),
          eq(churnEmails.currentRiskBand, currentRisk.band),
          sql`${churnEmails.createdAt} > NOW() - INTERVAL '7 days'`
        )
      );

    if (recentEmails.length > 0) return null; // Already sent recently

    // Only create email if member is in a risk band (not active)
    if (currentRisk.band === "active") return null;

    // Generate email using AI service
    const { generateChurnPreventionEmail } = await import("./services/geminiService");

    try {
      const emailContent = await generateChurnPreventionEmail(
        member,
        currentRisk.level,
        currentRisk.band
      );

      const emailData: InsertChurnEmail = {
        memberId: member.id,
        subject: emailContent.subject,
        content: emailContent.content,
        riskLevel: currentRisk.level,
        currentRiskBand: currentRisk.band,
        memberProfile: {
          firstName: member.firstName,
          lastName: member.lastName,
          email: member.email,
          membershipType: member.membershipType,
          joinDate: member.joinDate,
          lastVisit: member.lastVisit,
          loyaltyPoints: member.loyaltyPoints,
        },
        status: "pending",
      };

      return await this.createChurnEmail(emailData);
    } catch (error) {
      console.error("Error creating churn email:", error);
      return null;
    }
  }

  // Email interaction tracking operations
  async createEmailInteraction(interaction: InsertEmailInteraction): Promise<EmailInteraction> {
    const [emailInteraction] = await db
      .insert(emailInteractions)
      .values(interaction)
      .returning();
    return emailInteraction;
  }

  async getEmailInteractionsByProspect(email: string): Promise<EmailInteraction[]> {
    return await db
      .select()
      .from(emailInteractions)
      .where(eq(emailInteractions.prospectEmail, email))
      .orderBy(desc(emailInteractions.createdAt));
  }

  async getEmailInteractionByTrackingId(trackingId: string): Promise<EmailInteraction | undefined> {
    const [interaction] = await db
      .select()
      .from(emailInteractions)
      .where(eq(emailInteractions.trackingId, trackingId));
    return interaction;
  }

  async updateEmailInteraction(id: string, updates: Partial<EmailInteraction>): Promise<void> {
    await db
      .update(emailInteractions)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(emailInteractions.id, id));
  }
}

