    queryKey: ["/api/loyalty-offers"],
    retry: false,
  });

  const generateOffersMutation = useMutation({
    mutationFn: async (criteria: string) => {
      const response = await apiRequest("POST", "/api/ai/generate-loyalty-offers", {
