          </Card>
        </div>
      )}
    </div>
  );
}

server/services/emailService.ts.content:
import sgMail from '@sendgrid/mail';
import { Resend } from 'resend';
import { EmailTrackingService } from './emailTrackingService';
import { v4 as uuidv4 } from 'uuid';

// Initialize email services with API keys from environment
if (process.env.SENDGRID_API_KEY) {
  sgMail.setApiKey(process.env.SENDGRID_API_KEY);
}

const resend = process.env.RESEND_API_KEY ? new Resend(process.env.RESEND_API_KEY) : null;

interface EmailData {
  to: string;
  subject: string;
  content: string;
  prospectName: string;
}

type EmailProvider = 'sendgrid' | 'resend' | 'test';

export class EmailService {
  private static getAvailableProvider(): { provider: EmailProvider; configured: boolean } {
    console.log('ðŸ”§ Email Service Configuration Check:');
    console.log('  RESEND_API_KEY:', process.env.RESEND_API_KEY ? 'configured âœ…' : 'not set âŒ');
    console.log('  SENDGRID_API_KEY:', process.env.SENDGRID_API_KEY ? 'configured âœ…' : 'not set âŒ');

    if (process.env.RESEND_API_KEY) {
      console.log('  Selected provider: Resend');
      return { provider: 'resend', configured: true };
    }
    if (process.env.SENDGRID_API_KEY) {
      console.log('  Selected provider: SendGrid');
      return { provider: 'sendgrid', configured: true };
    }
    console.log('  Selected provider: Test mode');
    return { provider: 'test', configured: false };
  }

  static async sendSalesEmail(emailData: EmailData): Promise<{ success: boolean; messageId?: string; error?: string; trackingId?: string }> {
    const { provider, configured } = this.getAvailableProvider();

    try {
      // Generate unique tracking ID
      const trackingId = uuidv4();

      // Create tracking URLs
      const baseUrl = process.env.REPLIT_DOMAIN ? `https://${process.env.REPLIT_DOMAIN}` : 'http://localhost:5000';
      const trackingUrl = `${baseUrl}/api/track/${trackingId}`;
      const virtualTourUrl = `${baseUrl}/api/virtual-tour/${trackingId}`;

      // Replace placeholders in email content with tracking links
      const processedContent = emailData.content
        .replace(/\[Virgin Active Virtual Tour Link\]/g, virtualTourUrl)
        .replace(/\[PROSPECT_NAME\]/g, emailData.prospectName)
        .replace(/\[UNSUBSCRIBE_LINK\]/g, 'https://www.virginactive.com/unsubscribe')
        .replace(/\[HOME_PAGE_LINK\]/g, trackingUrl);

      // Add home page link if not already present
      const enhancedContent = processedContent.includes(trackingUrl) ? processedContent : 
        `${processedContent}\n\nExplore Virgin Active: ${trackingUrl}`;

      const result = await (async () => {
        switch (provider) {
          case 'resend':
            return await this.sendWithResend(emailData, enhancedContent);
          case 'sendgrid':
            return await this.sendWithSendGrid(emailData, enhancedContent);
          case 'test':
          default:
            return await this.sendTestEmail(emailData, enhancedContent);
        }
      })();

      // Log email interaction if sending was successful
      if (result.success) {
        await EmailTrackingService.logEmailSent(
          emailData.to,
          emailData.prospectName,
          emailData.subject,
          trackingId,
          {
            provider,
            messageId: result.messageId,
            timestamp: new Date().toISOString(),
          }
        );
      }

      return {
        ...result,
        trackingId
      };
    } catch (error: any) {
      console.error(`Email service error (${provider}):`, error);

      return {
        success: false,
        error: error.message || 'Failed to send email'
      };
    }
  }

  private static async sendWithResend(emailData: EmailData, processedContent: string): Promise<{ success: boolean; messageId?: string; error?: string }> {
    if (!resend) {
      return {
        success: false,
        error: 'Resend not configured. Please set RESEND_API_KEY environment variable.'
      };
    }

    const { data, error } = await resend.emails.send({
      from: 'Virgin Active <no-reply@virginactive.com>',
      to: emailData.to,
      subject: emailData.subject,
      text: processedContent,
      html: this.convertToHtml(processedContent),
    });

    if (error) {
      return {
        success: false,
        error: error.message || 'Failed to send email via Resend'
      };
    }

    return {
      success: true,
      messageId: data?.id
    };
  }

  private static async sendWithSendGrid(emailData: EmailData, processedContent: string): Promise<{ success: boolean; messageId?: string; error?: string }> {
    const msg = {
      to: emailData.to,
      from: {
        email: 'no-reply@virginactive.com',
        name: 'Virgin Active Sales Team'
      },
      subject: emailData.subject,
      text: processedContent,
      html: this.convertToHtml(processedContent),
      trackingSettings: {
        clickTracking: {
          enable: true,
          enableText: false
        },
        openTracking: {
          enable: true
        }
      }
    };

    const [response] = await sgMail.send(msg);

    return {
      success: true,
      messageId: response.headers['x-message-id'] as string
    };
  }

  private static async sendTestEmail(emailData: EmailData, processedContent: string): Promise<{ success: boolean; messageId?: string; error?: string }> {
    // Simulate email sending for testing/demo purposes
    console.log('ðŸ“§ TEST EMAIL MODE - Email would be sent:');
    console.log(`To: ${emailData.to}`);
    console.log(`Subject: ${emailData.subject}`);
    console.log(`Content: ${processedContent.substring(0, 100)}...`);

    // Simulate delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    return {
      success: true,
      messageId: `test-${Date.now()}`
    };
  }

  private static convertToHtml(textContent: string): string {
    // Convert text email to HTML with Virgin Active branding
    const lines = textContent.split('\n');
    const htmlLines = lines.map(line => {
      if (line.trim() === '') return '<br>';
      if (line.startsWith('Subject:')) {
        return `<h2 style="color: #e60012; font-family: Arial, sans-serif;">${line.replace('Subject:', '').trim()}</h2>`;
      }
      return `<p style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;">${line}</p>`;
    });

    return `
      <div style="max-width: 600px; margin: 0 auto; padding: 20px; font-family: Arial, sans-serif;">
        <div style="background-color: #e60012; padding: 20px; text-align: center; margin-bottom: 30px;">
          <h1 style="color: white; margin: 0; font-size: 24px; font-weight: bold;">VIRGIN ACTIVE</h1>
        </div>
        <div style="padding: 20px;">
          ${htmlLines.join('')}
        </div>
        <div style="background-color: #f8f9fa; padding: 20px; text-align: center; margin-top: 30px; border-top: 3px solid #e60012;">
          <p style="margin: 0; color: #666; font-size: 12px;">
            Â© Virgin Active. This email was sent from our Sales Team.<br>
            <a href="https://www.virginactive.com/unsubscribe" style="color: #e60012;">Unsubscribe</a> | 
            <a href="https://www.virginactive.com/privacy" style="color: #e60012;">Privacy Policy</a>
          </p>
        </div>
      </div>
    `;
  }

  static getEmailServiceStatus(): { provider: EmailProvider; configured: boolean; description: string } {
    const { provider, configured } = this.getAvailableProvider();

    const descriptions = {
      'resend': configured ? 'Resend configured âœ…' : 'Resend not configured',
      'sendgrid': configured ? 'SendGrid configured âœ…' : 'SendGrid not configured', 
      'test': 'Test mode - emails will be logged but not sent'
    };

    return {
      provider,
      configured,
      description: descriptions[provider]
    };
  }
}

server/services/emailTrackingService.ts.content:
import { db } from "../db";
import { emailInteractions, type EmailInteraction, type InsertEmailInteraction } from "@shared/schema";
import { eq, desc } from "drizzle-orm";

export class EmailTrackingService {
  // Create email interaction record
  static async createEmailInteraction(interaction: InsertEmailInteraction): Promise<EmailInteraction> {
    const [created] = await db
      .insert(emailInteractions)
      .values(interaction)
      .returning();
    return created;
  }

  // Get all interactions for a prospect
  static async getEmailInteractionsByProspect(email: string): Promise<EmailInteraction[]> {
    return await db
      .select()
      .from(emailInteractions)
      .where(eq(emailInteractions.prospectEmail, email))
      .orderBy(desc(emailInteractions.createdAt));
  }

  // Get interaction by tracking ID
  static async getEmailInteractionByTrackingId(trackingId: string): Promise<EmailInteraction | undefined> {
    const [interaction] = await db
      .select()
      .from(emailInteractions)
      .where(eq(emailInteractions.trackingId, trackingId));
    return interaction;
  }

  // Update email interaction
  static async updateEmailInteraction(id: string, updates: Partial<EmailInteraction>): Promise<void> {
    await db
      .update(emailInteractions)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(emailInteractions.id, id));
  }

  // Log when an email is sent
  static async logEmailSent(
    prospectEmail: string, 
    prospectName: string, 
    subject: string, 
    trackingId: string,
    metadata: any = {}
  ): Promise<EmailInteraction> {
    return await this.createEmailInteraction({
      prospectEmail,
      prospectName,
      interactionType: 'email_sent',
      emailSubject: subject,
      trackingId,
      metadata,
    });
  }

  // Log when a link is clicked
  static async logLinkClicked(trackingId: string, metadata: any = {}): Promise<void> {
    const interaction = await this.getEmailInteractionByTrackingId(trackingId);
    if (interaction) {
      // Create new interaction for link click
      await this.createEmailInteraction({
        prospectEmail: interaction.prospectEmail,
        prospectName: interaction.prospectName,
        interactionType: 'link_clicked',
        emailSubject: interaction.emailSubject,
        trackingId,
        metadata: { ...(interaction.metadata as any), ...metadata },
      });
    }
  }

  // Log when virtual tour is viewed
  static async logTourViewed(trackingId: string, metadata: any = {}): Promise<void> {
    const interaction = await this.getEmailInteractionByTrackingId(trackingId);
    if (interaction) {
      // Create new interaction for tour view
      await this.createEmailInteraction({
        prospectEmail: interaction.prospectEmail,
        prospectName: interaction.prospectName,
        interactionType: 'tour_viewed',
        emailSubject: interaction.emailSubject,
        trackingId,
        metadata: { ...(interaction.metadata as any), ...metadata },
      });
    }
  }

  // Generate tracking URL for emails
  static generateTrackingUrl(trackingId: string, baseUrl: string = ''): string {
    return `${baseUrl}/?track=${trackingId}`;
  }

  // Get engagement summary for a prospect
  static async getProspectEngagementSummary(email: string): Promise<{
    totalInteractions: number;
    emailsSent: number;
    linksClicked: number;
    toursViewed: number;
    lastInteraction?: Date;
  }> {
    const interactions = await this.getEmailInteractionsByProspect(email);

    const summary = {
      totalInteractions: interactions.length,
      emailsSent: interactions.filter(i => i.interactionType === 'email_sent').length,
      linksClicked: interactions.filter(i => i.interactionType === 'link_clicked').length,
      toursViewed: interactions.filter(i => i.interactionType === 'tour_viewed').length,
      lastInteraction: interactions.length > 0 ? interactions[0].createdAt || undefined : undefined,
    };

    return summary;
  }
}

client/src/pages/VirtualTour.tsx.content:
import React, { useState, useEffect } from 'react';

interface ProspectData {
  name: string;
  email: string;
  location: string;
  interest: string;
  fitnessLevel: string;
  goals?: string;
  preferredSchedule?: string;
}

interface TourMetrics {
  success: boolean;
  prospectName: string;
  prospectEmail: string;
  trackingId: string;
}

const VirtualTour: React.FC = () => {
  const [prospectData, setProspectData] = useState<ProspectData | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [trackingId, setTrackingId] = useState<string | null>(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const trackingParam = params.get('track');

    if (trackingParam) {
      setTrackingId(trackingParam);
      recordTourView(trackingParam);
    } else {
      setIsLoading(false);
    }
  }, []);

  const recordTourView = async (trackingIdParam: string) => {
    try {
      const response = await fetch(`/api/virtual-tour/${trackingIdParam}`);
      if (response.ok) {
        const tourMetrics: TourMetrics = await response.json();

        // Load CRM data to get prospect details
        const crmResponse = await fetch('/crm_data.json');
        const crmData = await crmResponse.json();

        // Find matching prospect by email
        const prospect = crmData.find((p: any) => 
          p.email === tourMetrics.prospectEmail ||
          p.name.toLowerCase().includes(tourMetrics.prospectName.toLowerCase())
        );

        if (prospect) {
          setProspectData({
            name: prospect.name,
            email: prospect.email,
            location: prospect.location,
            interest: prospect.interest,
            fitnessLevel: prospect.fitnessLevel,
            goals: prospect.goals,
            preferredSchedule: prospect.preferredSchedule,
          });
        }
      }
    } catch (error) {
      console.error('Failed to record tour view:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const renderPersonalizedContent = () => {
    if (!prospectData) {
      return renderDefaultTour();
    }

    return (
      <div className="bg-white">
        {/* Personalized Hero Section */}
        <div 
          className="relative h-96 bg-cover bg-center"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1534438327276-14e5300c3a48?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')",
          }}
        >
          <div className="absolute inset-0 bg-black bg-opacity-60" />
          <div className="relative z-10 flex items-center justify-center h-full">
            <div className="text-center text-white max-w-4xl mx-auto px-6">
              <h1 className="text-5xl font-bold mb-4">
                Welcome {prospectData.name.split(' ')[0]}!
              </h1>
              <p className="text-xl mb-6">
                Your personalized Virgin Active experience in {prospectData.location}
              </p>
              <p className="text-lg opacity-90">
                Based on your interest in {prospectData.interest.toLowerCase()}, 
                we've created a tour tailored just for you.
              </p>
            </div>
          </div>
        </div>

        {/* Personalized Facilities Section */}
        <div className="py-16 px-6">
          <div className="max-w-6xl mx-auto">
            <h2 className="text-3xl font-bold text-center mb-12 text-gray-900">
              Facilities Perfect for Your {prospectData.interest} Goals
            </h2>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {getPersonalizedFacilities(prospectData.interest).map((facility, index) => (
                <div key={index} className="bg-white rounded-lg shadow-lg overflow-hidden">
                  <img 
                    src={facility.image} 
                    alt={facility.name}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-6">
                    <h3 className="text-xl font-bold mb-2 text-gray-900">{facility.name}</h3>
                    <p className="text-gray-600 mb-4">{facility.description}</p>
                    <div className="text-red-600 font-medium">
                      Perfect for: {facility.suitableFor}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Personalized Classes Section */}
        <div className="bg-gray-50 py-16 px-6">
