// Test email sending functionality
import { EmailService } from './server/services/emailService';

async function testEmailSending() {
  console.log('ðŸ§ª Testing Email Service...\n');

  // Check email service status
  const status = EmailService.getEmailServiceStatus();
  console.log('ðŸ“§ Email Service Status:', status);
  console.log();

  // Test email data
  const testEmailData = {
    to: 'sdp01298@gmail.com',
    subject: 'Test Email from Member Buddy Platform ðŸ§ª',
    content: `Hello!

This is a test email from the Member Buddy platform to verify that email sending functionality is working correctly.

**Test Details:**
- Sent at: ${new Date().toISOString()}
- Provider: ${status.provider}
- Status: ${status.configured ? 'Configured âœ…' : 'Test mode'}

If you receive this email, the email functionality is working properly!

Best regards,
Member Buddy Platform Team`,
    prospectName: 'Test User'
  };

  console.log('ðŸ“¤ Sending test email to:', testEmailData.to);
  console.log('ðŸ“‹ Subject:', testEmailData.subject);
  console.log();

  try {
    const result = await EmailService.sendSalesEmail(testEmailData);

    if (result.success) {
      console.log('âœ… Email sent successfully!');
      console.log('ðŸ“§ Message ID:', result.messageId);
      console.log('ðŸ”— Tracking ID:', result.trackingId);
    } else {
      console.log('âŒ Email sending failed:');
      console.log('Error:', result.error);
    }

    return result;
  } catch (error) {
    console.error('ðŸ’¥ Exception during email sending:', error);
    return { success: false, error: error.message };
  }
}

// Run the test
testEmailSending()
  .then(result => {
    console.log('\nðŸ Test completed:', result.success ? 'SUCCESS' : 'FAILED');
    process.exit(result.success ? 0 : 1);
  })
  .catch(error => {
    console.error('ðŸ’¥ Test script error:', error);
    process.exit(1);
  });
